let result = msg.payload
if (result.gateway && result.layer && result.devices && result.layers){
    let gateway = result.gateway
    let gateways = await global.get("mysql_query")("select * from iot_gateway where macaddr = ? limit 1", gateway)
    if(gateways[0] == undefined){
        msg.error = "无效网关："+gateway
        return msg
    }

    //清理数据
    let where = ""
    for(let i = 0; i < result.layers.length; i++){
        where += " and layer != '"+result.layers[i]+"' "
    }
    let d_iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? " + where, gateways[0].id)
    for(let i = 0; i < d_iot_devices.length; i++){
        await global.get("mysql_query")("delete from iot_device_action where gatewayID = ? and deviceID = ?", [gateways[0].id, d_iot_devices[i].id])
        await global.get("mysql_query")("delete from iot_device where id = ?", d_iot_devices[i].id)
    }

    //现有库数据过滤被删除数据
    let s_iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ?", [gateways[0].id, result.layer])
    let s_iot_devices_obj = {}
    for (let i = 0; i < s_iot_devices.length; i++){
        s_iot_devices_obj[s_iot_devices[i].id] = true
    }


    let items = result.devices
    for(let i = 0; i < items.length; i++){
        let code = items[i].code
        let name = items[i].name
        let type = items[i].type
        let protocol = items[i].protocol
        let iotType = items[i].iottype
        let channel = items[i].channel
        let gatewayID = gateways[0].id
        let gatewayName = gateways[0].name
        let layer = result.layer
        let spaceCode = gateways[0].spaceCode
        let spaceName = gateways[0].spaceName
        let areaCode = gateways[0].areaCode
        let areaName = gateways[0].areaName
        let floorCode = gateways[0].floorCode
        let floorName = gateways[0].floorName
        let floorAreaType = ""
        let floorAreaCode = ""
        let floorAreaName = ""
        if(items[i].area){
            let areaArr = items[i].area.split("_")
            if(areaArr[2]){
                floorAreaType = areaArr[0]
                floorAreaCode = areaArr[1]
                floorAreaName = areaArr[2]
            }
        }

        let createtime = parseInt(new Date().getTime() / 1000)

        let values = [code, name, type, protocol, iotType, channel, gatewayID, gatewayName, layer, spaceCode, spaceName, areaCode, areaName, floorCode, floorName, floorAreaType, floorAreaCode, floorAreaName, createtime]
        let iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ? and code = ? limit 1", [gatewayID, layer, code])
        if (iot_devices[0] == undefined){
            await global.get("mysql_query")('insert into iot_device(code, name, type, protocol, iotType, channel, gatewayID, gatewayName, layer, spaceCode, spaceName, areaCode, areaName, floorCode, floorName, floorAreaType, floorAreaCode, floorAreaName, createtime) values (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)', values)
            iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ? and code = ? limit 1", [gatewayID, layer, code])
            if (iot_devices[0] == undefined){
                msg.error = "添加设备失败,code:" + code
                return msg
            }
        }else{
            await global.get("mysql_query")('update iot_device set code = ?, name = ?, type = ?, protocol = ?, iotType = ?, channel = ?, gatewayID = ?, gatewayName = ?, layer = ?, spaceCode = ?, spaceName = ?, areaCode = ?, areaName = ?, floorCode = ?, floorName = ?, floorAreaType = ?, floorAreaCode = ?, floorAreaName = ?, createtime = ? where id = "' + iot_devices[0].id +'" limit 1', values)
            if (s_iot_devices_obj[iot_devices[0].id]){
                delete s_iot_devices_obj[iot_devices[0].id]
            }
        }

        await global.get("mysql_query")("delete from iot_device_action where gatewayID = ? and deviceID = ?", [gatewayID, iot_devices[0].id])
        if (items[i].actions){
            let actions = items[i].actions
            for (let j = 0; j < actions.length; j++) {
                let gatewayID = gateways[0].id
                let deviceID = iot_devices[0].id
                let name = actions[j].name
                let type = actions[j].type
                let createtime = parseInt(new Date().getTime() / 1000)
                let actionValues = [gatewayID, deviceID, name, type, createtime]
                await global.get("mysql_query")('insert into iot_device_action(gatewayID, deviceID, name, type, createtime) values (?,?,?,?,?)', actionValues)
            }
        }
    }

    Object.keys(s_iot_devices_obj).forEach(async function(id) {
        await global.get("mysql_query")("delete from iot_device_action where gatewayID = ? and deviceID = ?", [gateways[0].id, id])
        await global.get("mysql_query")("delete from iot_device where id = ?", id)
    });
}
return msg;

