let macaddr = msg.payload.mac
let layer = msg.payload.layer
if(!macaddr || !layer){
    msg.error = "无效请求"
    return msg
}
let iot_gateways = await global.get("mysql_query")("select * from iot_gateway where deleteFlag = 1 and macaddr = ?", macaddr)
if (!iot_gateways[0]){
    msg.error = "无效网关"
    return msg
}

let result = {}
result.spaceCode = iot_gateways[0].spaceCode
result.spaceName = iot_gateways[0].spaceName
result.floorAreaCode = iot_gateways[0].areaCode
result.floorAreaName = iot_gateways[0].areaName

result.floors = []
let floor = {}
floor.floorCode = iot_gateways[0].floorCode
floor.floorName = iot_gateways[0].floorName
floor.areas = []

let floor_areas = await global.get("mysql_query")("select * from floor_area where deleteFlag = 1 and spaceCode = ? and areaCode = ? and floorCode = ?", [iot_gateways[0].spaceCode, iot_gateways[0].areaCode, iot_gateways[0].floorCode])
for(let i = 0; i < floor_areas.length; i++){
    floor.areas.push({
        areaCode:floor_areas[i].code,
        areaName: floor_areas[i].name,
        areaType: "area",
    })
}

let floor_pub_areas = await global.get("mysql_query")("select * from floor_pub_area where deleteFlag = 1 and spaceCode = ? and areaCode = ? and floorCode = ?", [iot_gateways[0].spaceCode, iot_gateways[0].areaCode, iot_gateways[0].floorCode])
for(let i = 0; i < floor_pub_areas.length; i++){
    floor.areas.push({
        areaCode:floor_pub_areas[i].code,
        areaName: floor_pub_areas[i].name,
        areaType: "pubArea",
    })
}

let floor_rooms = await global.get("mysql_query")("select * from floor_room where deleteFlag = 1 and spaceCode = ? and areaCode = ? and floorCode = ?", [iot_gateways[0].spaceCode, iot_gateways[0].areaCode, iot_gateways[0].floorCode])
for (let i = 0; i < floor_rooms.length; i++) {
    floor.areas.push({
        areaCode: floor_rooms[i].code,
        areaName: floor_rooms[i].name,
        areaType: "room",
    })
}

let floor_mrooms = await global.get("mysql_query")("select * from floor_mroom where deleteFlag = 1 and spaceCode = ? and areaCode = ? and floorCode = ?", [iot_gateways[0].spaceCode, iot_gateways[0].areaCode, iot_gateways[0].floorCode])
for (let i = 0; i < floor_mrooms.length; i++) {
    floor.areas.push({
        areaCode: floor_mrooms[i].code,
        areaName: floor_mrooms[i].name,
        areaType: "meetingroom",
    })
}

let floor_toilets = await global.get("mysql_query")("select * from floor_toilet where deleteFlag = 1 and spaceCode = ? and areaCode = ? and floorCode = ?", [iot_gateways[0].spaceCode, iot_gateways[0].areaCode, iot_gateways[0].floorCode])
for (let i = 0; i < floor_toilets.length; i++) {
    floor.areas.push({
        areaCode: floor_toilets[i].code,
        areaName: floor_toilets[i].name,
        areaType: "toilet",
    })
}
result.floors.push(floor)


result.devices = []
let iot_devices = await global.get("mysql_query")("select * from iot_device where deleteFlag = 1 and gatewayID = ? and layer = ?", [iot_gateways[0].id, layer])
for (let i = 0; i < iot_devices.length; i++) {
    let iot_device_actions = await global.get("mysql_query")("select name,type from iot_device_action where gatewayID = ? and deviceID = ?", [iot_gateways[0].id, iot_devices[i].id])

    //配置模式
    let typesObj = {
        airconditioning:[{
            name:"开",
            type:"on"
        },{
            name:"关",
            type:"off"
        },{
            name:"调温",
            type:"temperature"
        },{
            name:"模式",
            type:"mode"
        },{
            name:"风速",
            type:"fan"
        }
        // ,{
        //     name:"报警",
        //     type:"alarm"
        // }
        ],
        light: [{
            name: "开",
            type: "on"
        }, {
            name: "关",
            type: "off"
        }],
        pad: [{
            name: "开",
            type: "status"
        }, {
            name: "关",
            type: "play"
        }, {
            name: "关",
            type: "fullscreen"
        }, {
            name: "关",
            type: "bind"
        }, {
            name: "关",
            type: "refresh"
        }],
        airsensor:[{
            name: "",
            type: "pm25"
        },{
            name: "",
            type: "pm10"
        },{
            name: "",
            type: "tvoc"
        },{
            name: "",
            type: "co2"
        },{
            name: "",
            type: "formaldehyde"
        },{
            name: "",
            type: "temperature"
        },{
            name: "",
            type: "humidity"
        },{
            name: "",
            type: "light"
        }]
    }
    let modesObj = {
        airconditioning: {
            protocol:"TCP",
            channel:"RJ45"
        },
        light: {
            protocol: "modbus",
            channel: "RS485"
        },
        pad: {
            protocol:"TCP",
            channel:"RJ45"
        },
        airsensor: {
            protocol: "modbus",
            channel: "RS485"
        },
    }
    if (!iot_device_actions || iot_device_actions.length == 0){
        iot_device_actions = []
        if (typesObj[iot_devices[i].type]){
            for (let pp = 0; pp < typesObj[iot_devices[i].type].length; pp++){
                iot_device_actions.push({
                    gatewayID: iot_gateways[0].id,
                    deviceID: iot_devices[i].id,
                    name: typesObj[iot_devices[i].type][pp].name,
                    type: typesObj[iot_devices[i].type][pp].type
                })
            }
        }
    }
    if (!iot_devices[i].protocol && modesObj[iot_devices[i].type]){
        iot_devices[i].protocol = modesObj[iot_devices[i].type].protocol
        iot_devices[i].channel = modesObj[iot_devices[i].type].channel
    }
    result.devices.push({
        code: iot_devices[i].code,
        name: iot_devices[i].name,
        type: iot_devices[i].type,
        iottype: iot_devices[i].iotType,
        protocol: iot_devices[i].protocol,
        channel: iot_devices[i].channel,
        area: iot_devices[i].floorAreaType + "_" + iot_devices[i].floorAreaCode+ "_" + iot_devices[i].floorAreaName,
        actions: iot_device_actions
    })
}

msg.payload = result
return msg;

if(msg.payload && Array.isArray(msg.payload)){
    let devices = msg.payload
    for(let i = 0; i < devices.length; i++){
        let gateways = await global.get("mysql_query")("select * from iot_gateway where macaddr = ? limit 1", devices[i].gateway)
        if(gateways[0] == undefined){
            continue;
        }
        let iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ? and code = ? limit 1", [gateways[0].id, devices[i].layer, devices[i].code])
        if(iot_devices[0] == undefined){
            continue;
        }
        let status = devices[i].status
        if (!status || status.length <= 0 || status.online != 1){
            continue
        }
        let data = {}
        data.ts = new Date().getTime()
        data.position = "/" + iot_devices[0].spaceCode + "/" + iot_devices[0].areaCode + "/" + iot_devices[0].floorCode + "/" + iot_devices[0].floorAreaCode +"/" + iot_devices[0].name
        data.space = iot_devices[0].spaceCode
        data.floor_area = iot_devices[0].areaCode
        data.floor = iot_devices[0].floorCode
        data.area = iot_devices[0].floorAreaCode
        data.device_code = iot_devices[0].code
        data.device_id = iot_devices[0].id
        if(status.pm25 != undefined){
            data.pm25 = parseInt(status.pm25)
        }
        if(status.pm10 != undefined){
            data.pm10 = parseInt(status.pm10)
        }
        if (status.tvoc != undefined){
            data.tvoc = parseInt(status.tvoc)
        }
        if (status.co2 != undefined){
            data.co2 = parseInt(status.co2)
        }
        if (status.temperature != undefined) {
            data.temperature = parseFloat(status.temperature)
        }
        if (status.humidity != undefined) {
            data.humidity = parseFloat(status.humidity)
        }
        if (status.formaldehyde != undefined) {
            data.formaldehyde = parseFloat(status.formaldehyde)
        }
        if (status.noise != undefined) {
            data.noise = parseInt(status.noise)
        }
        if (status.light != undefined) {
            data.light = parseInt(status.light)
        }
        if (status.so2 != undefined) {
            data.so2 = parseInt(status.so2)
        }
        if (status.nh3 != undefined) {
            data.nh3 = parseInt(status.nh3)
        }
        if (status.o3 != undefined) {
            data.o3 = parseInt(status.o3)
        }
        if (status.o2 != undefined) {
            data.o2 = parseInt(status.o2)
        }
        if (status.h2s != undefined) {
            data.h2s = parseInt(status.h2s)
        }
        if (status.ch4 != undefined) {
            data.ch4 = parseInt(status.ch4)
        }
        if (status.co != undefined) {
            data.co = parseInt(status.co)
        }
        if (status.no2 != undefined) {
            data.no2 = parseInt(status.no2)
        }
        if (status.h2 != undefined) {
            data.h2 = parseInt(status.h2)
        }
        if (status.odor != undefined) {
            data.odor = parseInt(status.odor)
        }
        let newMsg = {}
        newMsg.payload = data
        node.send(newMsg)
    }
}

let items = msg.payload
if (items && Array.isArray(items) && items.length > 0){
    for (let i = 0; i < items.length; i++){
        let item = items[i]
        if (!item.gateway || !item.layer){
            continue;
        }
        let gateways = await global.get("mysql_query")("select * from iot_gateway where macaddr = ? limit 1", item.gateway)
        if(gateways[0] == undefined){
            continue;
        }
        let iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ? and code = ? and deleteFlag = 1 limit 1", [gateways[0].id, item.layer, item.code])
        if(iot_devices[0] == undefined){
            continue;
        }
        let status = JSON.stringify(item.status)
        let statusUptime = parseInt(new Date().getTime()/1000)
        await global.get("mysql_query")("update iot_device set status = ?, statusUptime = ? where id = ? ", [status, statusUptime, iot_devices[0].id])

        //如果空气数据异常走另外一个分支
        if(item.status){
            iot_devices[0].status = item.status
            iot_devices[0].statusUptime = statusUptime
            let newMsg = {}
            newMsg.payload = iot_devices[0]
            node.send(newMsg)
        }
    }
}

let device = msg.payload
let spaceId = flow.get("spaceId")
let spaceCode = flow.get("spaceCode")
if (!spaceId) {
    return
}
if (spaceCode != device.spaceCode){
    return
}
if (device && device.status) {
    let type = "" //设备类型报警
    let typeName = ""
    let deviceID = device.id
    let deviceName = device.name
    let deviceAddress = ""
    let deviceFullName = "/" + device.spaceCode + "/" + device.areaCode + "/" + device.floorCode + "/" + device.floorAreaCode + "/" + device.name
    let alarmCode = ""
    let alarmMsg = ""
    let baseUnit = ""
    let baseValue = ""
    let realValue = ""
    let createTime = parseInt(new Date().getTime() / 1000)
    let updateTime = createTime
    if (device.status.online != 1) {
        type = "1" //设备类型报警
        typeName = "设备告警"
        deviceAddress = "online"
        alarmCode = ""
        alarmMsg = "设备离线"
    } else if (device.status.alarm == "alarm") {
        type = "1" //设备类型报警
        typeName = "设备告警"
        alarmCode = device.status.alarmValue
        deviceAddress = "alarm"
        alarmMsg = "设备异常"
        if (device.type == "airconditioning"){
            type = "2"
            typeName = "空调告警"
            if (alarmCode == "162") {
                alarmMsg = "物业水循环异常"
            }else if (alarmCode == "16") {
                alarmMsg = "室外机组间通讯异常"
            }else if (alarmCode == "3") {
                alarmMsg = "室内外机或室内机间通讯异常"
            }
        }else if (device.type == "inundationsensor"){
            alarmMsg = "漏水异常，导电率"+alarmCode+"，值越大水量越大"
        }
    } else if (device.type == "airconditioning"){
        // if (device.status && device.status.pretemperature == "30" && device.status.mode == "3" && device.status.fan == "75"){
        //     type = "2"
        //     typeName = "空调告警"
        //     deviceAddress = "air_force_start"
        //     alarmCode = ""
        //     alarmMsg = "空调进入强启模式"
        // }
    } else if (device.type == "airsensor"){
        let alarmConfig = flow.get("alarmConfig")
        let workdays = [1, 2, 3, 4, 5]
        let weekdays = [0, 6]
        let year = moment().format("YYYY")
        let day = moment().format("MM-DD")
        let week = moment().day()
        let time = moment().format("HH:mm")
        let dayWeek = ""
        let senceDay = flow.get("senceDay")
        if (senceDay[year] && senceDay[year]["workday"] && Array.isArray(senceDay[year]["workday"]) && senceDay[year]["workday"].includes(day)) {
            dayWeek = "workday"
        } else if (senceDay[year] && senceDay[year]["holiday"] && Array.isArray(senceDay[year]["holiday"]) && senceDay[year]["holiday"].includes(day)) {
            dayWeek = "weekend"
        } else if (workdays.includes(week)) {
            dayWeek = "workday"
        } else if (weekdays.includes(week)) {
            dayWeek = "weekend"
        }
        if (!alarmConfig || !alarmConfig[device.spaceCode] || !alarmConfig[device.spaceCode][device.areaCode] || !alarmConfig[device.spaceCode][device.areaCode][device.floorCode]
            || !alarmConfig[device.spaceCode][device.areaCode][device.floorCode][device.floorAreaCode] || !alarmConfig[device.spaceCode][device.areaCode][device.floorCode][device.floorAreaCode][dayWeek]) {
            return
        }
        let alarmSet = alarmConfig[device.spaceCode][device.areaCode][device.floorCode][device.floorAreaCode][dayWeek]
        if (!(time >= alarmSet.startTime && time <= alarmSet.endTime)) {
            return
        }

        let temperature_low = alarmSet.temperature_low ? alarmSet.temperature_low : false
        let temperature_high = alarmSet.temperature_high ? alarmSet.temperature_high : false
        let humidity_low = alarmSet.humidity_low ? alarmSet.humidity_low : false
        let humidity_high = alarmSet.humidity_high ? alarmSet.humidity_high : false
        let pm25_high = alarmSet.pm25_high ? alarmSet.pm25_high : false
        let co2_high = alarmSet.co2_high ? alarmSet.co2_high : false
        let tvoc_high = alarmSet.tvoc_high ? alarmSet.tvoc_high : false
        let formaldehyde_high = alarmSet.formaldehyde_high ? alarmSet.formaldehyde_high : false

        if (temperature_low && device.status.temperature < temperature_low) {
            type = "3" //设备类型报警
            typeName = "环境告警"
            deviceAddress = "temperature_low"
            alarmCode = ""
            alarmMsg = "环境温度低于" + temperature_low + "℃异常"
            baseUnit = "℃"
            baseValue = temperature_low
            realValue = device.status.temperature
        }
        if (temperature_high && device.status.temperature >= temperature_high) {
            type = "3" //设备类型报警
            typeName = "环境告警"
            deviceAddress = "temperature_high"
            alarmCode = ""
            alarmMsg = "环境温度高于" + temperature_high + "℃异常"
            baseUnit = "℃"
            baseValue = temperature_high
            realValue = device.status.temperature
        }
        if (co2_high && device.status.co2 > co2_high) {
            type = "3" //设备类型报警
            typeName = "环境告警"
            deviceAddress = "co2_high"
            alarmCode = ""
            alarmMsg = "环境CO2高于" + co2_high + "ppm异常"
            baseUnit = "ppm"
            baseValue = co2_high
            realValue = device.status.co2
        }
    }

    if(!spaceId || !deviceID || !deviceAddress){
        return
    }

    //记录告警日志
    let values = [spaceId, type, typeName, deviceID, deviceName, deviceAddress, deviceFullName, alarmCode, alarmMsg, baseUnit, baseValue, realValue, createTime]
    await global.get("mysql_query")("insert into system_alarm (spaceId, type, typeName, deviceID, deviceName, deviceAddress, deviceFullName, alarmCode, alarmMsg, baseUnit, baseValue, realValue, createTime) values (?,?,?,?,?,?,?,?,?,?,?,?,?)", values)

    //推送日志信息到TD
    let data = {}
    data.ts = new Date().getTime()
    data.position = deviceFullName
    data.space = device.spaceCode
    data.floor_area = device.areaCode
    data.floor = device.floorCode
    data.area = device.floorAreaCode
    data.device_code = device.code
    data.device_id = device.id
    data.type = type
    data.type_name = typeName
    data.device_address = deviceAddress
    data.alarm_code = alarmCode
    data.alarm_msg = alarmMsg
    data.base_unit = baseUnit
    data.base_value = baseValue
    data.real_value = realValue
    let newMsg = {}
    newMsg.payload = data
    node.send(newMsg)

    //记录告警数据源
    let system_alarm_sources = await global.get("mysql_query")("select * from system_alarm_source where spaceId = ? and deviceID = ? and `status` = 0 and deviceAddress = ? limit 1", [spaceId, device.id, deviceAddress])
    if (system_alarm_sources[0] == undefined) {
        let values = [spaceId, type, typeName, deviceID, deviceName, deviceAddress, deviceFullName, alarmCode, alarmMsg, baseUnit, baseValue, realValue, createTime, updateTime]
