// 接收一个sql语句 以及所需的values
// 这里接收第二参数values的原因是可以使用mysql的占位符 '?'
// 比如 query(`select * from my_database where id = ?`, [1])

let mysql_format_to_tsh_sql = function(sql, index){
    if (!index){
        index = 1
    }
    if(sql.indexOf("?") == -1){
        return sql
    }else{
        sql = sql.replace("?", "$"+index)
        //node.error(sql)
        ++index
        return mysql_format_to_tsh_sql(sql, index)
    }
}

let mysql_format_to_tsh_sql_letter_lower_up_fix = function(sql){
    //node.error(sql)
    let newSql = ""
    if (sql.indexOf("insert into") != -1){
        let ls = sql.indexOf("(")
        let rs = sql.indexOf(")")
        let sql_pre = sql.substring(0, ls+1)
        let sql_last = sql.substring(rs)
        let sql_mid = sql.substring(ls+1, rs)
        let midArr = sql_mid.split(",")
        let sql_mid_new = ""
        for (let i = 0; i < midArr.length; i++) {
            let l = midArr[i].trim()
            if(i == midArr.length - 1){
                sql_mid_new += '\"' + l + '\"'
            }else{
                sql_mid_new += '\"' + l + '\",'
            }

        }
        newSql = sql_pre+sql_mid_new+sql_last
    }else{
        let sqlArr = sql.split(" ")
        let s = ["=", ">", "<", ">=", "<=", "!="]
        for (let i = 0; i < sqlArr.length; i++) {
            let l = sqlArr[i]
            if (s.includes(l) && sqlArr[i - 1]) {
                sqlArr[i - 1] = '\"' + sqlArr[i - 1] + '\"'
            }
        }

        for (let i = 0; i < sqlArr.length; i++) {
            newSql += sqlArr[i] + " "
        }
    }
    //node.error(newSql)
    return newSql
}

let mysql_query = async function (sql, values) {
    // 返回一个 Promise
    return new Promise((resolve, reject) => {
        pool.connect().then(connection => {
            sql = mysql_format_to_tsh_sql_letter_lower_up_fix(mysql_format_to_tsh_sql(sql))
            if (!Array.isArray(values)){
                values = [values]
            }
            connection.query(sql, values, (err, res) => {
                // 结束会话
                connection.release()
                if (err) {
                    reject(err)
                } else {
                    resolve(res.rows)
                }
            })
        }).catch(err => reject(err))
    })
}
global.set("mysql_query", mysql_query)

let sleep = function (ms) {
