        if(iot_devices[0] == undefined){
            continue;
        }
        let status = devices[i].status
        if(!status || status.length <= 0){
            continue
        }
        let data = {}
        data.ts = new Date().getTime()
        data.position = "/" + iot_devices[0].spaceCode + "/" + iot_devices[0].areaCode + "/" + iot_devices[0].floorCode + "/" + iot_devices[0].floorAreaCode +"/" + iot_devices[0].name
        data.space = iot_devices[0].spaceCode
        data.floor_area = iot_devices[0].areaCode
        data.floor = iot_devices[0].floorCode
        data.area = iot_devices[0].floorAreaCode
        data.device_code = iot_devices[0].code
        data.device_id = iot_devices[0].id
        if(status.voltage){
            data.voltage1 = parseFloat(parseFloat(status.voltage.A).toFixed(2))
            data.voltage2 = parseFloat(parseFloat(status.voltage.B).toFixed(2))
            data.voltage3 = parseFloat(parseFloat(status.voltage.C).toFixed(2))
        }
        if(status.power){
            for(let i = 1; i <= 27; i++){
                data["power" + i] = parseFloat(parseFloat(status.power["loop" + i]).toFixed(2))
            }
        }
        if(status.current){
            for(let i = 1; i <= 27; i++){
                data["current" + i] = parseFloat(parseFloat(status.current["loop" + i]).toFixed(2))
            }
        }
        if(status.kwh){
            for(let i = 1; i <= 27; i++){
                data["kwh" + i] = parseFloat(parseFloat(status.kwh["loop" + i]).toFixed(2))
            }
        }

        if(status.totalPower != undefined){
            data.total_power = parseFloat(parseFloat(status.totalPower).toFixed(2))
        }
        if (status.totalKwh != undefined){
            data.total_kwh = parseFloat(parseFloat(status.totalKwh).toFixed(2))
        }
        let newMsg = {}
        newMsg.payload = data
        node.send(newMsg)
    }
}

