        msgArr[0] = newMsg

        // let validFloors = ["23F", "24F", "25F", "26F", "27F", "28F", "29F", "30F", "31F", "32F"]
        let invalidAreas = {
            "25F":{"TMAN":true,"TMANVIP":true},
            "27F":{"TMAN":true,"TMANVIP":true},
            "28F":{"TMAN":true},
            "31F":{"TMAN":true,"TMANVIP":true},
            "41F":{"TMAN":true},
            "42F":{"TMAN":true,"TMANVIP":true},
            "45F":{"TMAN":true},
            "46F":{"TMAN":true,"TWOMAN":true},
            "54F":{"TMAN":true,"TWOMAN":true},
        }

        if (data.smoke === 1){

            if(invalidAreas[iot_devices[0].floorCode] && invalidAreas[iot_devices[0].floorCode][iot_devices[0].floorAreaCode]){
                continue
            }

            //记录到告警中心
            let uuid = flow.get("uuid")()
            let spaceCode = iot_devices[0].spaceCode
            let floorAreaCode = iot_devices[0].areaCode
            let floorCode = iot_devices[0].floorCode
            let areaCode = iot_devices[0].floorAreaCode
