            let classify = "security"
            let classifyName = "安防告警"
            let emergentType = "烟雾探测"
            let floorCodeNum = parseInt(floorCode)
            let floorName = floorCode
            if (floorCodeNum) {
                floorName = floorCodeNum + "楼"
            }
            let emergentContent = "请注意" + floorName + sourceName + "产生报警，请立即前往！"
            let voiceText = emergentContent  //语音文本规则？
            let voiceUrl = ""
            if (status.smokingSourceImg) {
                voiceUrl = status.smokingSourceImg
            }
            let happenDate = moment().format("YYYY-MM-DD")
            let happenTime = moment().format("YYYY-MM-DD HH:mm:ss")
            let createTime = parseInt(new Date().getTime() / 1000)
            let values = [uuid, spaceCode, floorAreaCode, floorCode, areaCode, sourceName, classify, classifyName, emergentType, emergentContent, voiceText, voiceUrl, happenDate, happenTime, createTime]

            let system_emergents = await global.get("mysql_query")("select * from system_emergent where spaceCode = ? and floorAreaCode = ? and floorCode = ? and areaCode = ? and sourceName = ? and classify = ? and emergentType = ? and status != 2 and status != 3 limit 1", [spaceCode, floorAreaCode, floorCode, areaCode, sourceName, classify, emergentType])
            if (system_emergents[0] == undefined) {
                await global.get("mysql_query")('insert into system_emergent (uuid, spaceCode, floorAreaCode, floorCode, areaCode, sourceName, classify, classifyName, emergentType, emergentContent, voiceText, voiceUrl, happenDate, happenTime, createTime) values (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)', values)
                let pushMsg = {}
                pushMsg.payload = {
                    uuid: uuid,
                    spaceCode: spaceCode,
                    floorAreaCode: floorAreaCode,
                    floorCode: floorCode,
                    areaCode: areaCode,
                    sourceName: sourceName,
                    happenTime: happenTime,
                    message: emergentContent
                }
                msgArr[1] = pushMsg
            }
        }
        node.send(msgArr)
    }
}

