if(msg.payload && msg.payload.code == "200" && msg.payload.data && msg.payload.data.actionData){
    let data = {}
    data.actionData = msg.payload.data.actionData
    let policys = data
    if (!policys['actionData'] || !Array.isArray(policys['actionData'])) {
        return
    }
    let actionData = policys['actionData']
    let alarmConfig = {}
    for (let i = 0; i < actionData.length; i++) {
        let item1 = actionData[i]
        if (!item1.action) {
            continue
        }
        let itemAction = item1.action
        for (let j = 0; j < itemAction.length; j++) {
            let item = itemAction[j]
            if (item.senceType != "alerm" || !item.action || !item.action.conditions || !Array.isArray(item.action.conditions)) {
                continue
            }
            // node.error(item)
            if (!item.realadress || !Array.isArray(item.realadress)) {
                continue
            }
            let dayType = item.dayType
            let startTime = "00:00"
            let endTime = "23:59"
            if (item.startTime) {
                startTime = item.startTime
            }
            if (item.endTime) {
                endTime = item.endTime
            }
            let temperature_low = false//18
            let temperature_high = false//27
            let pm25_high = false//75
            let co2_high = false//1000
            let tvoc_high = false//0.6
            let conditions = item.action.conditions
            for (let k = 0; k < conditions.length; k++) {
                let c = conditions[k]
                if (c.condition == "<" && c.temperature != undefined) {
                    temperature_low = c.temperature
                } else if (c.condition == ">" && c.temperature != undefined) {
                    temperature_high = c.temperature
                } else if (c.condition == ">" && c.type == "pm2.5") {
                    pm25_high = c.value
                } else if (c.condition == ">" && c.type == "co2") {
                    co2_high = c.value
                } else if (c.condition == ">" && c.type == "TVOC") {
                    tvoc_high = c.value
                }
            }

            let areas = item.realadress
            for (let m = 0; m < areas.length; m++) {
                let a = areas[m]
                if (!a.spaceCode || !a.floorAreaCode || !a.floorCode || !a.areaCode) {
                    continue
                }
                if (alarmConfig[a.spaceCode] == undefined) {
                    alarmConfig[a.spaceCode] = {}
                }
                if (alarmConfig[a.spaceCode][a.floorAreaCode] == undefined) {
                    alarmConfig[a.spaceCode][a.floorAreaCode] = {}
                }
                if (alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode] == undefined) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode] = {}
                }
                if (alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode] == undefined) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode] = {}
                }
                if (alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType] == undefined) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType] = {}
                }
                alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['startTime'] = startTime
                alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['endTime'] = endTime
                if (temperature_low) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['temperature_low'] = temperature_low
                }
                if (temperature_high) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['temperature_high'] = temperature_high
                }
                if (pm25_high) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['pm25_high'] = pm25_high
                }
                if (co2_high) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['co2_high'] = co2_high
                }
                if (tvoc_high) {
                    alarmConfig[a.spaceCode][a.floorAreaCode][a.floorCode][a.areaCode][dayType]['tvoc_high'] = tvoc_high
                }
            }

        }
    }
    flow.set("alarmConfig", alarmConfig)
    msg.config = alarmConfig
}
return msg;

