 * 高并发 IoT 数据入库逻辑
 * 功能：解析 MQTT/Batch 数据，生成标准 SQL 字符串，并彻底净化 msg 对象
 */

// 1. 初始化数据源（兼容单条消息或 Batch 消息）
const messages = Array.isArray(msg.payload) ? msg.payload : [msg];
let sqlStatements = [];
let stats = { total: messages.length, matched: 0, skipped: 0 };

const TYPE_CONFIG = {
    metrics: ['airsensor', 'airconditioning', 'aircleaner', 'smokesensor', 'watersensor', 'wcsensor', 'inundationsensor', 'pad'],
    status_only: ['light', 'dlight', 'airfan', 'blind', 'humensensor', 'door', 'aicamerasensor']
};

messages.forEach((m, idx) => {
    // 提取有效数据载体
    const data = m.payload || {};
    const topic = data.originalTopic || m.topic || "";

    // 2. 解析元数据
    let category = "";
    let campus = "", building = "", floor = "", area = "";
    if (topic) {
        const parts = topic.split('/');
        category = parts[3] || "";
        campus = parts[4] || "";
        building = parts[5] || "";
        floor = parts[6] || "";
        area = parts[7] || "";
    }

    // 检查核心字段：没有 code 直接跳过
    if (!data.code) {
        node.warn(`[跳过] 第 ${idx} 条：缺少 device code`);
        stats.skipped++;
        return;
    }

    // 处理时间与转义
    const statusObj = data.status || {};
    const safeCode = data.code.replace(/'/g, "''");
    const safeGateway = (data.gateway || '').replace(/'/g, "''");
    const safeStatusData = JSON.stringify(statusObj).replace(/'/g, "''");

    let isMatched = false;

    // --- 分支匹配逻辑 ---

    // 逻辑 1：电量
    if (category === 'powersensor') {
        sqlStatements.push(`INSERT INTO iot_power_data (ts, code, gateway, total_power, total_kwh, details) VALUES (CURRENT_TIMESTAMP, '${safeCode}', '${safeGateway}', ${statusObj.totalPower || 0}, ${statusObj.totalKwh || 0}, '${safeStatusData}')`);
        isMatched = true;
    }

    // 逻辑 2：指标
    if (TYPE_CONFIG.metrics.includes(category)) {
        sqlStatements.push(`INSERT INTO iot_sensor_metrics (ts, code, category, gateway, campus, building, floor, area, metrics) VALUES (CURRENT_TIMESTAMP, '${safeCode}', '${category}', '${safeGateway}', '${campus}', '${building}', '${floor}', '${area}', '${safeStatusData}')`);
        isMatched = true;
    }

    // 逻辑 3：状态
    if (statusObj.status !== undefined) {
        const currentStatus = statusObj.status.toString().replace(/'/g, "''");
        const online = statusObj.online !== undefined ? statusObj.online : 1;
        sqlStatements.push(`INSERT INTO iot_device_status (ts, code, category, status, online, raw_status) VALUES (CURRENT_TIMESTAMP, '${safeCode}', '${category}', '${currentStatus}', ${online}, '${safeStatusData}')`);
        isMatched = true;
    }

    if (isMatched) stats.matched++;
    else stats.skipped++;
});

// 3. 构建输出
if (sqlStatements.length > 0) {
    // 1. 合并 SQL
    const finalSql = sqlStatements.join('; ') + ';';

    node.status({ fill: "green", shape: "dot", text: `已生成: ${stats.matched} 条 SQL` });

    // 2. 【核心修复】按照插件文档要求，将 SQL 放入 msg.query
    // 同时为了保险，payload 也放一份，但真正起作用的是 query
    return {
        query: finalSql,
        payload: finalSql
    };
} else {
    node.status({ fill: "yellow", shape: "ring", text: "本批次无有效数据" });
    return null;
}

