    let items = res.data
    let column = res.column_meta
    let result = []
    for(let i = 0 ;i < items.length; i++) {
        let item = {}
        for(let j = 0 ;j < items[i].length; j++) {
            if(column[j] && column[j][0]){
                if(column[j][0] == "temperature"){
                    item[column[j][0]] = parseFloat(parseFloat(items[i][j]).toFixed(1))
                }else{
                    item[column[j][0]] = parseFloat(parseFloat(items[i][j]).toFixed(0))
                }
            }
        }
        result.push(item)
    }
    if (result[0]){
        flow.set("spaceAirsensorAvg", result[0])
    }
    msg.payload.result = result
}
return msg;

