let spaceCode = flow.get("spaceCode")
let today = moment().format("YYYY-MM-DD")
let currentTime = parseInt(new Date().getTime()/1000)
let checkTime = currentTime - 600
let floor_mrooms = await global.get("mysql_query")("select * from floor_mroom where spaceCode = ? and deleteFlag = 1 order by floorCode asc", [spaceCode])
let iot_devices = await global.get("mysql_query")("select * from iot_device where spaceCode = ? and deleteFlag = 1 and type = 'humensensor' and floorAreaType = 'meetingroom'", [spaceCode])
let mroomSensorObj = {}
for (let i = 0; i < iot_devices.length; i++){
    let s = iot_devices[i]
    //判定是否有人
    if (s.statusUptime && s.statusUptime >= checkTime){
        let status = flow.get("jsJson")(s.status)
        //占用状态
        if(status && status.online == 1 && status.status == "busy"){
            mroomSensorObj[s.floorAreaCode] = 1
        }
    }
}

let mroomUseCountObj = {}
mroomUseCountObj["allCount"] = 0
mroomUseCountObj["allUseCount"] = 0


mroomUseCountObj["floors"] = {}

for (let i = 0; i < floor_mrooms.length; i++){
    let m = floor_mrooms[i]

    //总计数
    ++mroomUseCountObj["allCount"]
    if (mroomSensorObj[m.code] == 1){
        ++mroomUseCountObj["allUseCount"]
    }

    //分区计数
    if (mroomUseCountObj[m.areaCode+"Count"] == undefined){
        mroomUseCountObj[m.areaCode + "Count"] = 0
    }
    if (mroomUseCountObj[m.areaCode + "UseCount"] == undefined) {
        mroomUseCountObj[m.areaCode + "UseCount"] = 0
    }
    ++mroomUseCountObj[m.areaCode + "Count"]
    if (mroomSensorObj[m.code] == 1) {
        ++mroomUseCountObj[m.areaCode + "UseCount"]
    }

    //楼层计数
    if (mroomUseCountObj["floors"][m.areaCode] == undefined) {
        mroomUseCountObj["floors"][m.areaCode] = {}
    }
    if (mroomUseCountObj["floors"][m.areaCode][m.floorCode] == undefined){
        mroomUseCountObj["floors"][m.areaCode][m.floorCode] = {
            "allCount":0,
            "useCount": 0
        }
    }
    ++mroomUseCountObj["floors"][m.areaCode][m.floorCode]["allCount"]
    if (mroomSensorObj[m.code] == 1) {
        ++mroomUseCountObj["floors"][m.areaCode][m.floorCode]["useCount"]
    }
}

mroomUseCountObj["allUnUseCount"] = mroomUseCountObj["allCount"] - mroomUseCountObj["allUseCount"]
mroomUseCountObj["A3UseRate"] = mroomUseCountObj["A3Count"] && mroomUseCountObj["A3UseCount"] ? parseInt(mroomUseCountObj["A3UseCount"] / mroomUseCountObj["A3Count"]*100) : 0
mroomUseCountObj["A4UseRate"] = mroomUseCountObj["A4Count"] && mroomUseCountObj["A4UseCount"] ? parseInt(mroomUseCountObj["A4UseCount"] / mroomUseCountObj["A4Count"] * 100) : 0
mroomUseCountObj["A5UseRate"] = mroomUseCountObj["A5Count"] && mroomUseCountObj["A5UseCount"] ? parseInt(mroomUseCountObj["A5UseCount"] / mroomUseCountObj["A5Count"] * 100) : 0

mroomUseCountObj["A3FloorCounts"] = []
mroomUseCountObj["A4FloorCounts"] = []
mroomUseCountObj["A5FloorCounts"] = []
//组装楼层数据
let areaKeys = Object.keys(mroomUseCountObj["floors"])
for (let i = 0; i < areaKeys.length; i++){
    let areaCode = areaKeys[i]
    let floorKeys = Object.keys(mroomUseCountObj["floors"][areaCode])
    for(let j = 0; j < floorKeys.length; j++){
        let floorCode = floorKeys[j]
        let floorCountObj = mroomUseCountObj["floors"][areaCode][floorCode]
        mroomUseCountObj[areaCode+"FloorCounts"].push({
            "floor": parseInt(floorCode) + "",
            "use": floorCountObj["useCount"] ? floorCountObj["useCount"] : 0,
            "unuse": floorCountObj["allCount"] ? floorCountObj["allCount"] - floorCountObj["useCount"] : 0
        })
    }
}

flow.set("mroomUseCountObj", mroomUseCountObj)
msg.mroomUseCountObj = mroomUseCountObj
return msg;

