let aes128CBCEncrypt = function (data) {
    const cipher = crypto.createCipheriv('aes-128-cbc', flow.get("DataSecret"), flow.get("DataSecretIV"));
    cipher.setAutoPadding(true);
    const encrypted = cipher.update(data, 'utf8', 'base64');
    return encrypted + cipher.final('base64');
};
