let biHmacMd5Sign = function (method, uri, timeStr, queryStr) {
    let str = ""
    str += "REQ-BODY" + queryStr
    str += "REQ-URL" + uri
    str += "X-APP-CODE" + flow.get("biAppCode")
    str += "X-DATE"+timeStr
    str += "X-HMAC-ALGORITHM"+"MD5"
    str += flow.get("biAppsecret")
    return crypto.createHash("md5").update(str, "utf-8").digest("hex").toUpperCase()
}
flow.set("biHmacMd5Sign", biHmacMd5Sign)

