let data = msg.payload
if (data && data.code == "200" && data.data && Array.isArray(data.data.dataValue)){
    let items = data.data.dataValue
    let hourPersonCount = {}
    for (let i = 0; i < items.length; i++){
        let item = items[i]
        let time = item.dateHour.split(" - ")[0].trim()
        if (!time){
            continue
        }
        let h = moment(time, "YYYY-MM-DD HH:mm:ss").hour()
        if(h < 10){
            h = "0"+h
        }
        hourPersonCount[h] = {
            "hm": item["ZK00291"],
            "ib": item["ZK00290"]
        }

    }
    //用餐人数
    let todayEachHourHM = []
    //在楼人数
    let todayEachHourIB = []
    let nowHour = new Date().getHours()
    let today = moment().format("MM月DD日")


    for (let i = 0; i < 24; i++) {
        let key = i + ""
        if (i < 10) {
            key = "0" + i
        }
        let hm = 0
        if (i < nowHour && hourPersonCount[key] && hourPersonCount[key]["hm"]) {
            hm = hourPersonCount[key]["hm"]
        }
        let ib = 0
        if (i < nowHour && hourPersonCount[key] && hourPersonCount[key]["ib"]) {
            ib = hourPersonCount[key]["ib"]
        }
        todayEachHourHM.push({
            "datetime": today + i + "点",
            "time": key,
            "number": hm
        })
        todayEachHourIB.push({
            "datetime": today + i + "点",
            "time": key,
            "number": ib
        })
    }
    flow.set("todayEachHourHM", todayEachHourHM)
    flow.set("todayEachHourIB", todayEachHourIB)
    msg.todayEachHourHM = todayEachHourHM
    msg.todayEachHourIB = todayEachHourIB
    msg.hourPersonCount = hourPersonCount
}
return msg;

let data = msg.payload
