        let iot_devices = await global.get("mysql_query")("select * from iot_device where gatewayID = ? and layer = ? and code = ? limit 1", [gateways[0].id, devices[i].layer, devices[i].code])
        if(iot_devices[0] == undefined){
            continue;
        }
        let status = devices[i].status
        if(!status){
            continue
        }
        if(status.densitycount == undefined){
            continue
        }
        let data = {}
        data.ts = new Date().getTime()
        data.position = "/" + iot_devices[0].spaceCode + "/" + iot_devices[0].areaCode + "/" + iot_devices[0].floorCode + "/" + iot_devices[0].floorAreaCode +"/" + iot_devices[0].name
        data.space = iot_devices[0].spaceCode
        data.floor_area = iot_devices[0].areaCode
        data.floor = iot_devices[0].floorCode
        data.area = iot_devices[0].floorAreaCode
        data.device_code = iot_devices[0].code
        data.device_id = iot_devices[0].id
        data.people_count = parseInt(status.densitycount)
        data.img_url = status.densitycountSourceImg
        let newMsg = {}
        newMsg.payload = data
        node.send(newMsg)
    }
}

